<?phpinclude_once($_SERVER['DOCUMENT_ROOT'] . '/lib/common.php');$webhelper = new WebHelper();$returnUrl = $webhelper->RequestFilter("returnUrl", -1, false);$error = $webhelper->RequestFilter("error", 0, false);if($error == "1")    $message = "ID or Password is incorrect.";else    $message = "";if(!$returnUrl) $returnUrl = urlencode ("/admin/manager/list.php");?><!DOCTYPE html><html lang="en-US"><head>    <meta charset="UTF-8">    <meta name="viewport" content="width=device-width, initial-scale=1">    <title>Login | Purigen Biosystems</title>    <link rel="canonical" href="https://www.purigenbio.com/" />    <link rel="stylesheet" href="https://use.fontawesome.com/releases/v5.11.2/css/all.css">    <link rel="stylesheet" href="/admin/css/index.css">    <script src="https://kit.fontawesome.com/a929b7cf75.js" crossorigin="anonymous"></script>    <script src="https://code.jquery.com/jquery-3.4.1.min.js" integrity="sha256-CSXorXvZcTkaix6Yvo6HppcZGetbYMGWSFlBw8HfCJo=" crossorigin="anonymous"></script>    <script src="/admin/scripts/ui.js"></script>    <script type="text/javascript">        function checkEnter(e)        {            if(e.keyCode == 13)            {                checkFrm();            }        }        function checkFrm()        {            var f = document.loginFrm;            var isOk = true;            $("#user_id").css("border", "");            $("#user_pswd").css("border", "");            if(f.user_id.value == "")            {                $("#user_id").css("border", "1px solid #ff0000");                isOk = false;            }            if(f.user_pswd.value == "")            {                $("#user_pswd").css("border", "1px solid #ff0000");                isOk = false;            }            if(isOk)            {                f.submit();            }        }    </script></head><body>    <div id="wrap">        <div class="login">            <div class="login-box">                <h1><img src="/admin/images/logo-purigen-white.svg" alt="purigen biosystems"/></h1>                <h2>ADMIN LOGIN <?=$message?></h2>                <form method="post" name="loginFrm" action="/admin/login.php">                    <input type="hidden" name="returnUrl" value="<?=$returnUrl?>"/>                    <div class="login-field">                        <input type="text" name="user_id" id="user_id" onkeypress="checkEnter(event);" placeholder="User ID"/>                        <input type="password" name="user_pswd" id="user_pswd" onkeypress="checkEnter(event);" placeholder="Password"/>                    </div>                </form>                <div class="login-submit">                    <button onclick="checkFrm();">LOG IN</button>                </div>                <div class="login-copyright">                    Â© <strong>2019 Purigen Biosystems, Inc.</strong> All rights reserved.                </div>            </div>        </div>        <!-- //login-->    </div>    <!-- //wrap --></body></html>