<?phpinclude_once($_SERVER['DOCUMENT_ROOT'] . '/lib/common.php');$webhelper = new WebHelper();$dbhelper = new dbHelper();$PageUrl = "/admin/login.php";$PageCode = "000000";$id = $webhelper->RequestFilter("user_id", 20, false);$passwd = $webhelper->RequestFilter("user_pswd", 20, false);$returnUrl = $webhelper->RequestFilter("returnUrl", -1, FALSE);if($webhelper->isNull($id)) $webhelper->AlertMessage("Please enter a valid Username.");if($webhelper->isNull($passwd)) $webhelper->AlertMessage("Please enter a valid Password.");$dbhelper->dbOpen();$sql = "select pkid, firstname, isAdministrator, isEditor, isCareers from admin where status = 1 and id = '" . $id . "' and pass = '" . $passwd . "'";$admin = $dbhelper->RunSQLReturnOneRow($sql);$dbhelper->dbClose();if( $admin["pkid"] == ""){    $webhelper->AlertMessageAndGo("", "/admin/?returnUrl=" . $returnUrl . "&error=1");}else{    $dbhelper->dbOpen();    $sql = "update admin set logindate = now() where pkid = " . $admin["pkid"];    $dbhelper->RunSQL($sql);    $dbhelper->dbClose();}$webhelper->SetCookie("adminside_purigenbiosystems_com_adminside_user_pkid", $admin["pkid"]);$webhelper->SetCookie("adminside_purigenbiosystems_com_adminside_user_id", $id);$webhelper->SetCookie("adminside_purigenbiosystems_com_adminside_user_name", $admin["firstname"]);$webhelper->SetCookie("adminside_purigenbiosystems_com_adminside_isadministrator", $admin["isAdministrator"]);$webhelper->SetCookie("adminside_purigenbiosystems_com_adminside_iseditor", $admin["isEditor"]);$webhelper->SetCookie("adminside_purigenbiosystems_com_adminside_iscareers", $admin["isCareers"]);if($admin["isAdministrator"] == "0" && $admin["isEditor"] == "0" && $admin["isCareers"] == "1")    $webhelper->AlertMessageAndGo("", "/admin/company/careers/list.php");else    $webhelper->AlertMessageAndGo("", urldecode($returnUrl));?>